import React, { useState } from 'react';

const MarketEventEditor = () => {
    const [events, setEvents] = useState([
        {
            area: "雙北",
            name: "台北圓山花博",
            date: "11/22.23.24",
            time: "10:00-19:00",
            address: "臺北市台北市中山區玉門街1號"
        }
    ]);

    const [newEvent, setNewEvent] = useState({
        area: "",
        name: "",
        date: "",
        time: "",
        address: ""
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEvent(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleAddEvent = () => {
        if (!newEvent.area || !newEvent.name || !newEvent.date || !newEvent.time || !newEvent.address) {
            alert('請填寫所有欄位');
            return;
        }
        setEvents(prev => [...prev, newEvent]);
        setNewEvent({
            area: "",
            name: "",
            date: "",
            time: "",
            address: ""
        });
    };

    const handleDeleteEvent = (index) => {
        setEvents(prev => prev.filter((_, i) => i !== index));
    };

    const styles = {
        container: {
            padding: '20px',
            maxWidth: '800px',
            margin: '0 auto',
        },
        card: {
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '20px',
            backgroundColor: 'white',
        },
        input: {
            width: '100%',
            padding: '8px',
            margin: '8px 0',
            border: '1px solid #ddd',
            borderRadius: '4px',
        },
        label: {
            display: 'block',
            marginBottom: '4px',
            fontWeight: 'bold',
        },
        button: {
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '4px',
            cursor: 'pointer',
            marginTop: '10px',
        },
        deleteButton: {
            backgroundColor: '#dc3545',
            color: 'white',
            border: 'none',
            padding: '5px 10px',
            borderRadius: '4px',
            cursor: 'pointer',
        },
        eventCard: {
            border: '1px solid #eee',
            borderRadius: '4px',
            padding: '15px',
            marginTop: '10px',
            backgroundColor: '#f8f9fa',
        },
        eventHeader: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'flex-start',
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2 style={{ marginBottom: '20px' }}>新增市集活動</h2>
                <div>
                    <label style={styles.label}>地區</label>
                    <input
                        style={styles.input}
                        name="area"
                        value={newEvent.area}
                        onChange={handleInputChange}
                        placeholder="例：雙北"
                    />
                </div>
                <div>
                    <label style={styles.label}>活動名稱</label>
                    <input
                        style={styles.input}
                        name="name"
                        value={newEvent.name}
                        onChange={handleInputChange}
                        placeholder="例：台北圓山花博"
                    />
                </div>
                <div>
                    <label style={styles.label}>日期</label>
                    <input
                        style={styles.input}
                        name="date"
                        value={newEvent.date}
                        onChange={handleInputChange}
                        placeholder="例：11/22.23.24"
                    />
                </div>
                <div>
                    <label style={styles.label}>時間</label>
                    <input
                        style={styles.input}
                        name="time"
                        value={newEvent.time}
                        onChange={handleInputChange}
                        placeholder="例：10:00-19:00"
                    />
                </div>
                <div>
                    <label style={styles.label}>地址</label>
                    <input
                        style={styles.input}
                        name="address"
                        value={newEvent.address}
                        onChange={handleInputChange}
                        placeholder="例：臺北市中山區玉門街1號"
                    />
                </div>
                <button
                    style={styles.button}
                    onClick={handleAddEvent}
                >
                    新增活動
                </button>
            </div>

            <div style={styles.card}>
                <h2 style={{ marginBottom: '20px' }}>活動列表</h2>
                {events.map((event, index) => (
                    <div key={index} style={styles.eventCard}>
                        <div style={styles.eventHeader}>
                            <div>
                                <p>📌{event.area}📌</p>
                                <p>🏝️ {event.name}</p>
                                <p>📅 {event.date} {event.time}</p>
                                <p>📍 {event.address}</p>
                            </div>
                            <button
                                style={styles.deleteButton}
                                onClick={() => handleDeleteEvent(index)}
                            >
                                刪除
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MarketEventEditor;
