import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Test() {
    const [newsData, setNewsData] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');

    useEffect(() => {
        async function fetchNews() {
            try {
                const response = await axios.get('http://localhost:5000/api/get_HomeNews');
                setNewsData(response || []); // 確保回傳值為陣列
            } catch (error) {
                setResponseMessage(error.response?.data?.error || '無法取得初始資料');
            }
        }
        fetchNews();
    }, []);

    return (
        <div>
            <h1>新聞列表</h1>
            {responseMessage && <p style={{ color: 'red' }}>{responseMessage}</p>}
            <ul>
                {newsData.map((item, index) => (
                    <li key={index}>
                        <h3>{item.Title}</h3>
                        <p>{item.Content}</p>
                        <img src={item.Image_Url} alt={item.Title} style={{ width: '100px' }} />
                        <p>發布時間: {new Date(item.Publish_Date).toLocaleDateString()}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Test;
