// ProductData/index.js
import React, { useState } from 'react';
import axios from 'axios';

function InputTitle() {
    const [inputData, setInputData] = useState('');
    const [responseMessage, setResponseMessage] = useState('');

    function handleInputChange(e) {
        setInputData(e.target.value);
    }

    async function handleSubmit() {
        if (!inputData.trim()) {
            setResponseMessage('請輸入標題');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5000/api/save_HomeData', {
                Title: inputData,
                CustomerUuid: 'example-customer-uuid',  // 替換為實際值
                TitleImg: '',                          // 若有圖片路徑可在這裡補上
                Title_Status: 1                        // 根據需求更改狀態
            });
            setResponseMessage(response.data.message || '操作成功');
        } catch (error) {
            setResponseMessage(error.response?.data?.error || '發生錯誤');
        }
    }

    return (
        <div className="input-group">
            <h1>商品管理</h1>
            <input
                className="input-box"
                type="text"
                value={inputData}
                onChange={handleInputChange}
                placeholder="輸入標題"
            />
            <button className="submit-button" onClick={handleSubmit}>send</button>
            <a>顯示訊息: {responseMessage}</a>
        </div>
    );
}

export default InputTitle;
