import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Test() {
    const [newsData, setNewsData] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchNews() {
            try {
                const response = await axios.get('http://localhost:5000/api/get_HomeNews');
                console.log('API 響應:', response.data); // 調試用
                
                if (response.data && Array.isArray(response.data.news)) {
                    setNewsData(response.data.news);
                } else {
                    setResponseMessage('數據格式不正確');
                }
            } catch (error) {
                console.error('獲取新聞錯誤:', error);
                setResponseMessage(error.response?.data?.error || '無法取得資料');
            } finally {
                setLoading(false);
            }
        }
        fetchNews();
    }, []);

    const formatDate = (dateString) => {
        if (!dateString) return '日期未設定';
        try {
            const date = new Date(dateString);
            if (isNaN(date.getTime())) {
                return '日期格式錯誤';
            }
            return date.toLocaleDateString('zh-TW', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
            });
        } catch (e) {
            console.error('日期格式化錯誤:', e);
            return '日期格式錯誤';
        }
    };

    if (loading) {
        return <div>載入中...</div>;
    }

    return (
        <div className="news-container">
            <h1>新聞列表</h1>
            {responseMessage && <p className="error-message">{responseMessage}</p>}
            {newsData.length === 0 ? (
                <p className="no-news">暫無新聞</p>
            ) : (
                <ul className="news-list">
                    {newsData.map((item, index) => (
                        <li key={item.Id || index} className="news-item">
                            <h3 className="news-title">{item.Title}</h3>
                            <p className="news-content">{item.Content}</p>
                            {item.Image_Url && (
                                <img 
                                    src={item.Image_Url} 
                                    alt={item.Title} 
                                    className="news-image"
                                    onError={(e) => {
                                        e.target.style.display = 'none';
                                        console.log('圖片載入失敗:', item.Image_Url);
                                    }}
                                />
                            )}
                            <p className="news-date">
                                發布時間: {formatDate(item.Publish_Date)}
                            </p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default Test;
