import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Test() {
    const [newsData, setNewsData] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');

    useEffect(() => {
        async function fetchNews() {
            try {
                const response = await axios.get('http://localhost:5000/api/get_HomeNews');
                console.log('API 響應:', response.data); // 調試用
                
                // 確保我們使用正確的數據結構
                if (response.data && Array.isArray(response.data.news)) {
                    setNewsData(response.data.news);
                } else {
                    setResponseMessage('數據格式不正確');
                }
            } catch (error) {
                console.error('獲取新聞錯誤:', error);
                setResponseMessage(error.response?.data?.error || '無法取得資料');
            }
        }
        fetchNews();
    }, []);

    const formatDate = (dateString) => {
        if (!dateString) return '';
        try {
            return new Date(dateString).toLocaleDateString();
        } catch (e) {
            return dateString;
        }
    };

    return (
        <div>
            <h1>新聞列表</h1>
            {responseMessage && <p style={{ color: 'red' }}>{responseMessage}</p>}
            {newsData.length === 0 ? (
                <p>暫無新聞</p>
            ) : (
                <ul>
                    {newsData.map((item, index) => (
                        <li key={item.Id || index}>
                            <h3>{item.Title}</h3>
                            <p>{item.Content}</p>
                            {item.Image_Url && (
                                <img 
                                    src={item.Image_Url} 
                                    alt={item.Title} 
                                    style={{ width: '100px' }} 
                                    onError={(e) => {
                                        e.target.style.display = 'none';
                                    }}
                                />
                            )}
                            <p>發布時間: {formatDate(item.Publish_Date)}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default Test;
