import React, { useState } from 'react';
import './InputImg.css';

function InputImg() {
    const [image, setImage] = useState("/images/market.jpg"); // 預設圖片
    const [showModal, setShowModal] = useState(false); // 控制模態視窗
    const [fileInput, setFileInput] = useState(null); // 用於觸發檔案選擇

    // 處理圖片選擇事件
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const imageUrl = URL.createObjectURL(file);
            
            // 儲存檔案到指定位置（模擬）
            saveImageToPath(file, "/images/market.jpg");
            
            // 更新顯示的圖片
            setImage(imageUrl);
        }
    };

    // 模擬儲存檔案到指定路徑的函數
    const saveImageToPath = async (file, path) => {
        try {
            // 在實際專案中，這裡應該使用後端API來處理檔案儲存
            console.log(`嘗試儲存檔案 ${file.name} 到 ${path}`);
            
            // 實際專案中的程式碼可能類似:
            // const formData = new FormData();
            // formData.append('file', file);
            // await axios.post('/api/upload', formData, {
            //     params: { path: '/images/market.jpg' }
            // });
        } catch (error) {
            console.error("儲存檔案時發生錯誤:", error);
        }
    };

    // 觸發檔案選擇
    const triggerFileInput = () => {
        if (fileInput) {
            fileInput.click();
        }
    };

    // 開啟模態視窗
    const openModal = () => {
        setShowModal(true);
    };

    // 關閉模態視窗
    const closeModal = () => {
        setShowModal(false);
    };

    return (
        <div className="input-img-container">
            <div className="image-container">
                <img 
                    src={image} 
                    alt="Title Image" 
                    className="title-image" 
                    onClick={openModal}
                />
                <div className="image-overlay">
                    <input
                        type="file"
                        ref={(input) => setFileInput(input)}
                        accept="image/*"
                        onChange={handleImageChange}
                        style={{ display: 'none' }}
                    />
                    <button 
                        className="change-image-btn"
                        onClick={triggerFileInput}
                    >
                        <i className="fa fa-upload"></i> 更換圖片
                    </button>
                </div>
            </div>

            {/* 模態視窗 */}
            {showModal && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close-btn" onClick={closeModal}>
                            &times;
                        </span>
                        <img src={image} alt="預覽" className="modal-image" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default InputImg;
