//D:\Carl\React\test\src\pages\Home\index.js

import React from 'react';
import { useAuth } from '../../context/AuthContext';
import {
  Container,
  Button,
  StyledLink,
  Nav,
  NavList,
  NavItem
} from '../../components/StyledComponents';

const Home = () => {
  const { user, logout } = useAuth();

  return (
    <Container>
      <h1>首頁</h1>
      <Nav>
        <NavList>
          <NavItem><StyledLink to="/page1">Page 1</StyledLink></NavItem>
          <NavItem><StyledLink to="/page2">Page 2 (Protected)</StyledLink></NavItem>
          <NavItem><StyledLink to="/page3">Page 3</StyledLink></NavItem>
        </NavList>
      </Nav>
      {user ? (
        <div>
          <p>Welcome, {user.username}!</p>
          <Button onClick={logout}>登出</Button>
        </div>
      ) : (
        <StyledLink to="/login">登入</StyledLink>
      )}
    </Container>
  );
};

export default Home;
