// C:\類D槽\Python\StockWeb\webtest\src\pages\Page1\HomeData\components\Data

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Data.css';

function Data() {
    const [loginKeyData, setLoginKeyData] = useState([]);
    const [editIndex, setEditIndex] = useState(null);
    const [newData, setNewData] = useState({
        Uuid: '',
        Name: '',
        LoginKey: '',
        Description: '',
    });

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get('http://localhost:5000/api/get_loginkey');
                setLoginKeyData(response.data);
            } catch (error) {
                console.error(error);
            }
        }
        fetchData();
    }, []);

    const handleEdit = (index) => {
        setEditIndex(index);
        setNewData({ ...loginKeyData[index] });
    };

    const handleChange = (e) => {
        setNewData({
            ...newData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSave = async (id) => {
        try {
            await axios.put(`http://localhost:5000/api/update_loginkey/${id}`, newData);
            setEditIndex(null);
            const updatedData = loginKeyData.map(item => item.Id === id ? newData : item);
            setLoginKeyData(updatedData);
        } catch (error) {
            console.error(error);
        }
    };

    const handleAdd = () => {
        setEditIndex(loginKeyData.length); // 設定為最後一個位置，顯示新增表單
        setNewData({
            Uuid: '',
            Name: '',
            LoginKey: generateRandomLoginKey(),
            Description: '',
        });
    };
    

    const generateRandomLoginKey = () => {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        return Array.from({ length: 20 }, () => characters[Math.floor(Math.random() * characters.length)]).join('');
    };

    return (
        <div className="data-container">
            <h2>Login Key Management</h2>
            <button onClick={handleAdd} className="add-button">Add New</button>
            {loginKeyData.map((data, index) => (
                <div key={data.Id} className="data-row">
                    {editIndex === index ? (
                        <div className="edit-row">
                            <input name="Name" value={newData.Name} onChange={handleChange} placeholder="Name" />
                            <input name="LoginKey" value={newData.LoginKey} onChange={handleChange} placeholder="Login Key" />
                            <textarea name="Description" value={newData.Description} onChange={handleChange} placeholder="Description"></textarea>
                            <button onClick={() => handleSave(data.Id)} className="save-button">Save</button>
                        </div>
                    ) : (
                        <div className="display-row">
                            <span>ID: {data.Id}</span>
                            <span>UUID: {data.Uuid}</span>
                            <span>Name: {data.Name}</span>
                            <span>LoginKey: {data.LoginKey}</span>
                            <span>CreatedAt: {data.CreatedAt}</span>
                            <button onClick={() => handleEdit(index)} className="edit-button">Edit</button>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
}

export default Data;
