import React, { useState } from 'react';
import axios from 'axios';
import './InputImg.css';

function InputImg() {
    const [image, setImage] = useState("/images/market.jpg");
    const [showModal, setShowModal] = useState(false);
    const [fileInput, setFileInput] = useState(null);

    // 處理圖片上傳
    // 處理圖片上傳
    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (file) {
            const formData = new FormData();
            formData.append('file', file);
            formData.append('Title', 'HomeTitle');
            formData.append('TitleImg', file.name);
            formData.append('Title_Status', 1);

            try {
                // 呼叫 Flask API 上傳圖片
                const response = await axios.post('/api/Change_HomeImg', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });

                if (response.status === 200) {
                    // 更新本地顯示的圖片
                    const imageUrl = URL.createObjectURL(file);
                    setImage(imageUrl);
                    alert('上傳成功');
                }
            } catch (error) {
                console.error('上傳圖片失敗:', error.response ? error.response.data : error);
                alert('上傳圖片失敗：' + (error.response ? error.response.data.error : error.message));
            }
        }
    };

    // 觸發檔案選擇
    const triggerFileInput = () => {
        if (fileInput) {
            fileInput.click();
        }
    };

    // 開啟模態視窗
    const openModal = () => {
        setShowModal(true);
    };

    // 關閉模態視窗
    const closeModal = () => {
        setShowModal(false);
    };

    return (
        <div className="input-img-container">
            <div className="image-container">
                <img 
                    src={image} 
                    alt="Title Image" 
                    className="title-image" 
                    onClick={openModal}
                />
                <div className="image-overlay">
                    <input
                        type="file"
                        ref={(input) => setFileInput(input)}
                        accept="image/*"
                        onChange={handleImageUpload}
                        style={{ display: 'none' }}
                    />
                    <button 
                        className="change-image-btn"
                        onClick={triggerFileInput}
                    >
                        <i className="fa fa-upload"></i> 更換圖片
                    </button>
                </div>
            </div>

            {/* 模態視窗 */}
            {showModal && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close-btn" onClick={closeModal}>
                            &times;
                        </span>
                        <img src={image} alt="預覽" className="modal-image" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default InputImg;
