const MarketTitle = () => {
    const location = useLocation();

    // Check if the current path is inside /page1 (including sub-routes)
    if (location.pathname.startsWith('/page1')) {
        return null; // Return null to hide the navigation on Page1 routes
    }

    return (
        <nav>
            <ul>
                {navItems.map((item) => (
                    <li key={item.id}>
                        <Link to={item.path}>{item.title}</Link>
                    </li>
                ))}
                <li>
                    <Link to="/login">登入</Link>
                </li>
            </ul>
        </nav>
    );
};
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <AuthProvider>
            <Router>
                <div>

                    <MarketTitle />  {/*This will conditionally render the navigation */}
                    <Routes>
                    <Route path="/" element={<Navigate to="/page3" replace />} />
                    <Route path="/page3" element={<Page3 />} />
                        {/* <Route path="/new-data" element={<NewData />} />
                        <Route path="/register" element={<Register />} /> */}
                    </Routes>
                </div>
            </Router>
        </AuthProvider>
    </React.StrictMode>
);
