import React, { useState, useEffect } from 'react';
import { PlusCircle, Edit, Trash2, Save, X } from 'lucide-react';
import './NewsManager.css';
import axios from 'axios';

const NewsManager = () => {
    const [newsData, setNewsData] = useState([]);
    const [editingItem, setEditingItem] = useState(null);
    const [responseMessage, setResponseMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [newItem, setNewItem] = useState({
        title: '',
        content: '',
        publishDate: new Date().toISOString().split('T')[0],
        status: 1,  // 1: 發布, 0: 未發布
        immediatePublish: true  // 是否立即發布
    });

    // 獲取新聞列表
    const fetchNews = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/get_HomeNews');
            if (response.data && Array.isArray(response.data.news)) {
                setNewsData(response.data.news);
            } else {
                setResponseMessage('數據格式不正確');
            }
        } catch (error) {
            console.error('獲取新聞錯誤:', error);
            setResponseMessage(error.response?.data?.error || '無法取得資料');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchNews();
    }, []);

    // 新增新聞
    const handleAdd = async () => {
        try {
            const newsData = {
                title: newItem.title,
                content: newItem.content,
                publishDate: newItem.immediatePublish ? new Date().toISOString() : newItem.publishDate,
                status: newItem.immediatePublish ? 1 : 0
            };

            const response = await axios.post('http://localhost:5000/api/add_news', newsData);
            if (response.data.success) {
                fetchNews();  // 重新獲取新聞列表
                setNewItem({  // 重置表單
                    title: '',
                    content: '',
                    publishDate: new Date().toISOString().split('T')[0],
                    status: 1,
                    immediatePublish: true
                });
            }
        } catch (error) {
            console.error('新增新聞失敗:', error);
            setResponseMessage('新增失敗：' + error.response?.data?.message);
        }
    };

    // 更新新聞
    const handleUpdate = async (id) => {
        try {
            const response = await axios.put(`http://localhost:5000/api/update_news/${id}`, editingItem);
            if (response.data.success) {
                fetchNews();
                setEditingItem(null);
            }
        } catch (error) {
            console.error('更新新聞失敗:', error);
            setResponseMessage('更新失敗：' + error.response?.data?.message);
        }
    };

    // 刪除新聞
    const handleDelete = async (id) => {
        if (window.confirm('確定要刪除這則新聞嗎？')) {
            try {
                const response = await axios.delete(`http://localhost:5000/api/delete_news/${id}`);
                if (response.data.success) {
                    fetchNews();
                }
            } catch (error) {
                console.error('刪除新聞失敗:', error);
                setResponseMessage('刪除失敗：' + error.response?.data?.message);
            }
        }
    };

    if (loading) return <div>載入中...</div>;

    // 將新聞分類
    const publishedNews = newsData.filter(news => news.Status === 1);
    const unpublishedNews = newsData.filter(news => news.Status === 0);

    return (
        <div className="news-manager">
            <h2>最新消息管理</h2>
            
            {/* 新增表單 */}
            <div className="news-form">
                <h3>新增消息</h3>
                <input
                    type="text"
                    placeholder="標題"
                    value={newItem.title}
                    onChange={(e) => setNewItem({...newItem, title: e.target.value})}
                />
                <textarea
                    placeholder="內容"
                    value={newItem.content}
                    onChange={(e) => setNewItem({...newItem, content: e.target.value})}
                />
                <div className="publish-options">
                    <label>
                        <input
                            type="checkbox"
                            checked={newItem.immediatePublish}
                            onChange={(e) => setNewItem({
                                ...newItem,
                                immediatePublish: e.target.checked
                            })}
                        />
                        立即發布
                    </label>
                    {!newItem.immediatePublish && (
                        <input
                            type="datetime-local"
                            value={newItem.publishDate}
                            onChange={(e) => setNewItem({
                                ...newItem,
                                publishDate: e.target.value
                            })}
                        />
                    )}
                </div>
                <button onClick={handleAdd}>
                    <PlusCircle size={16} />
                    新增
                </button>
            </div>

            {/* 發布中的新聞 */}
            <div className="news-section">
                <h3>發布中的新聞</h3>
                <div className="news-list">
                    {publishedNews.map(item => (
                        <NewsItem
                            key={item.Id}
                            item={item}
                            onEdit={setEditingItem}
                            onUpdate={handleUpdate}
                            onDelete={handleDelete}
                            editingItem={editingItem}
                            setEditingItem={setEditingItem}
                        />
                    ))}
                </div>
            </div>

            {/* 未發布的新聞 */}
            <div className="news-section">
                <h3>未發布的新聞</h3>
                <div className="news-list">
                    {unpublishedNews.map(item => (
                        <NewsItem
                            key={item.Id}
                            item={item}
                            onEdit={setEditingItem}
                            onUpdate={handleUpdate}
                            onDelete={handleDelete}
                            editingItem={editingItem}
                            setEditingItem={setEditingItem}
                        />
                    ))}
                </div>
            </div>

            {responseMessage && (
                <div className="response-message">
                    {responseMessage}
                </div>
            )}
        </div>
    );
};

// 新聞項目組件
const NewsItem = ({ item, onEdit, onUpdate, onDelete, editingItem, setEditingItem }) => {
    const isEditing = editingItem?.Id === item.Id;

    return (
        <div className="news-item">
            {isEditing ? (
                <div className="news-edit-form">
                    <input
                        type="text"
                        value={editingItem.Title}
                        onChange={(e) => setEditingItem({
                            ...editingItem,
                            Title: e.target.value
                        })}
                    />
                    <textarea
                        value={editingItem.Content}
                        onChange={(e) => setEditingItem({
                            ...editingItem,
                            Content: e.target.value
                        })}
                    />
                    <input
                        type="datetime-local"
                        value={editingItem.Publish_Date?.split('.')[0]}
                        onChange={(e) => setEditingItem({
                            ...editingItem,
                            Publish_Date: e.target.value
                        })}
                    />
                    <div className="button-group">
                        <button onClick={() => onUpdate(item.Id)}>
                            <Save size={16} />
                            保存
                        </button>
                        <button onClick={() => setEditingItem(null)}>
                            <X size={16} />
                            取消
                        </button>
                    </div>
                </div>
            ) : (
                <div className="news-content">
                    <h4>{item.Title}</h4>
                    <p>{item.Content}</p>
                    <p className="news-date">
                        發布時間: {new Date(item.Publish_Date).toLocaleString()}
                    </p>
                    <div className="button-group">
                        <button onClick={() => onEdit(item)}>
                            <Edit size={16} />
                            編輯
                        </button>
                        <button onClick={() => onDelete(item.Id)}>
                            <Trash2 size={16} />
                            刪除
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default NewsManager;
