//C:\類D槽\Python\StockWeb\webtest\src\context\AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkSession = async () => {
      try {
        const response = await axios.get('http://localhost:3001/auth', { withCredentials: true });
        if (response.data.loggedIn) {
          setUser(response.data.user); // �p�G session ���ġA�N user �]���n�J���A
        }
      } catch (error) {
        console.error('Error checking session:', error);
      }
      setLoading(false);
    };

    checkSession();
  }, []);

  const login = (userData) => {
    setUser(userData);
  };

  const logout = async () => {
    await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
    setUser(null); // �M�� user ���A
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
