import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { navItems } from './data/users'; // 更改為 users.js
import { PrivateRoute } from './components/PrivateRoute';
import Home from './pages/Home';
import NewData from './pages/Page1/NewData';
import HomeData from './pages/Page1/HomeData';
import LoginKey from './pages/Page1/LoginKey';
import Register from './pages/Register';
import Login from './pages/Login';
import Page1 from './pages/Page1';
import Page2 from './pages/Page2';
import Page3 from './pages/Page3';
import ProductData from './pages/Page1/ProductData';
import PortfolioData from './pages/Page1/PortfolioData';

const Navigation = () => {
    const location = useLocation();

    // Check if the current path is inside /page1 (including sub-routes)
    if (location.pathname.startsWith('/page1')) {
        return null; // Return null to hide the navigation on Page1 routes
    }

    return (
        <nav>
            <ul>
                {navItems.map((item) => (
                    <li key={item.id}>
                        <Link to={item.path}>{item.title}</Link>
                    </li>
                ))}
                <li>
                    <Link to="/login">登入</Link>
                </li>
            </ul>
        </nav>
    );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <AuthProvider>
            <Router>

                <div>
                    <Navigation /> {/* This will conditionally render the navigation */}
                    <Route path="/" element={<Navigate to="/page3" replace />} />
                    <Routes>
                        {/* <Route path="/login" element={<Login />} />
