import React, { useState, useEffect } from 'react';
import { PlusCircle, Edit, Trash2, Save, X } from 'lucide-react';
import './NewsManager.css'
import axios from 'axios';

const NewsManager = () => {
    const [newsItems, setNewsItems] = useState([]);
    const [editingItem, setEditingItem] = useState(null);
    const [newItem, setNewItem] = useState({
        title: '',
        content: '',
        date: new Date().toISOString().split('T')[0]
    });
    const [newsData, setNewsData] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchNews() {
            try {
                const response = await axios.get('http://localhost:5000/api/get_HomeNews');
                console.log('API 響應:', response.data); // 調試用
                
                if (response.data && Array.isArray(response.data.news)) {
                    setNewsData(response.data.news);
                } else {
                    setResponseMessage('數據格式不正確');
                }
            } catch (error) {
                console.error('獲取新聞錯誤:', error);
                setResponseMessage(error.response?.data?.error || '無法取得資料');
            } finally {
                setLoading(false);
            }
        }
        fetchNews();
        fetchNews_M();
    }, []);

    const formatDate = (dateString) => {
        if (!dateString) return '日期未設定';
        try {
            const date = new Date(dateString);
            if (isNaN(date.getTime())) {
                return '日期格式錯誤';
            }
            return date.toLocaleDateString('zh-TW', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
            });
        } catch (e) {
            console.error('日期格式化錯誤:', e);
            return '日期格式錯誤';
        }
    };

    if (loading) {
        return <div>載入中...</div>;
    }




    const fetchNews_M = async () => {
        try {
            const response = await fetch('/api/news');
            const data = await response.json();
            setNewsItems(data);
        } catch (error) {
            console.error('獲取新聞失敗:', error);
        }
    };

    // 新增新聞
    const handleAdd = async () => {
        try {
            const response = await fetch('/api/news', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newItem)
            });
            if (response.ok) {
                fetchNews_M();
                setNewItem({
                    title: '',
                    content: '',
                    date: new Date().toISOString().split('T')[0]
                });
            }
        } catch (error) {
            console.error('新增失敗:', error);
        }
    };

    // 更新新聞
    const handleUpdate = async (id) => {
        try {
            const response = await fetch(`/api/news/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(editingItem)
            });
            if (response.ok) {
                fetchNews_M();
                setEditingItem(null);
            }
        } catch (error) {
            console.error('更新失敗:', error);
        }
    };

    // 刪除新聞
    const handleDelete = async (id) => {
        if (window.confirm('確定要刪除這則新聞嗎？')) {
            try {
                const response = await fetch(`/api/news/${id}`, {
                    method: 'DELETE'
                });
                if (response.ok) {
                    fetchNews_M();
                }
            } catch (error) {
                console.error('刪除失敗:', error);
            }
        }
    };

    return (
        <div className="news-manager">
            <h2>最新消息管理</h2>
            
            {/* 新增表單 */}
            <div className="news-form">
                <h3>新增消息</h3>
                <input
                    type="date"
                    value={newItem.date}
                    onChange={(e) => setNewItem({...newItem, date: e.target.value})}
                />
                <input
                    type="text"
                    placeholder="標題"
                    value={newItem.title}
                    onChange={(e) => setNewItem({...newItem, title: e.target.value})}
                />
                <textarea
                    placeholder="內容"
                    value={newItem.content}
                    onChange={(e) => setNewItem({...newItem, content: e.target.value})}
                />
                <button onClick={handleAdd}>
                    <PlusCircle size={16} />
                    新增
                </button>
            </div>

            {/* 新聞列表 */}
            <div className="news-list">
                {newsItems.map(item => (
                    <div key={item.id} className="news-item">
                        {editingItem?.id === item.id ? (
                            // 編輯模式
                            <div className="news-edit-form">
                                <input
                                    type="date"
                                    value={editingItem.date.split('T')[0]}
                                    onChange={(e) => setEditingItem({
                                        ...editingItem,
                                        date: e.target.value
                                    })}
                                />
                                <input
                                    type="text"
                                    value={editingItem.title}
                                    onChange={(e) => setEditingItem({
                                        ...editingItem,
                                        title: e.target.value
                                    })}
                                />
                                <textarea
                                    value={editingItem.content}
                                    onChange={(e) => setEditingItem({
                                        ...editingItem,
                                        content: e.target.value
                                    })}
                                />
                                <div className="button-group">
                                    <button onClick={() => handleUpdate(item.id)}>
                                        <Save size={16} />
                                        保存
                                    </button>
                                    <button onClick={() => setEditingItem(null)}>
                                        <X size={16} />
                                        取消
                                    </button>
                                </div>
                            </div>
                        ) : (
                            // 顯示模式
                            <div className="news-content">
                                <div className="news-date">{new Date(item.date).toLocaleDateString()}</div>
                                <div className="news-title">{item.title}</div>
                                <div className="news-text">{item.content}</div>
                                <div className="button-group">
                                    <button onClick={() => setEditingItem(item)}>
                                        <Edit size={16} />
                                        編輯
                                    </button>
                                    <button onClick={() => handleDelete(item.id)}>
                                        <Trash2 size={16} />
                                        刪除
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>

            <h1>新聞列表</h1>
            {responseMessage && <p className="error-message">{responseMessage}</p>}
            {newsData.length === 0 ? (
                <p className="no-news">暫無新聞</p>
            ) : (
                <ul className="news-list">
                    {newsData.map((item, index) => (
                        <li key={item.Id || index} className="news-item">
                            <h3 className="news-title">{item.Title}</h3>
                            <p className="news-content">{item.Content}</p>
                            {item.Image_Url && (
                                <img 
                                    src={item.Image_Url} 
                                    alt={item.Title} 
                                    className="news-image"
                                    onError={(e) => {
                                        e.target.style.display = 'none';
                                        console.log('圖片載入失敗:', item.Image_Url);
                                    }}
                                />
                            )}
                            <p className="news-date">
                                發布時間: {formatDate(item.Publish_Date)}
                            </p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default NewsManager;
