//Login/InputJs.js
import React, { useState, useEffect } from 'react';
import { FaExclamationCircle } from 'react-icons/fa';
import { Tooltip } from 'react-tooltip';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { AuthProvider } from '../../context/AuthContext';
import './index.css';

function Index() {
  const [inputEaildata, setinputEaildata] = useState('');
  const [inputPWDdata, setinputPWDdata] = useState('');
  const [EmailData, setEmailData] = useState('');
  const [isEmpty, setIsEmpty] = useState(false);
  const [isFormatWrong, setisFormatWrong] = useState(false);
  const [isNoEmail, setisNoEmail] = useState(false);
  const [showInputPwd, setshowInputPwd] = useState(false);
  const [isPWDEmpty, setisPWDEmpty] = useState(false);
  const [isPWDWorng, setisPWDWorng] = useState(false);
  const [isLoginSuccess, setisLoginSuccess] = useState(false);

  const inputEmaildataChange = (e) => {
    setinputEaildata(e.target.value);
    setIsEmpty(false); // 重置错误状态
    setisFormatWrong(false); // 重置错误状态
    setisNoEmail(false)
    setshowInputPwd(false)
  };

  const inputPWDdataChange = (e) => {
    setinputPWDdata(e.target.value);
    setisPWDEmpty(false)
    setisPWDWorng(false)
  };

  const inputEailData = async (e) => {
    e.preventDefault();
    if (inputEaildata === '') {
      setIsEmpty(true);
      return;
    }
    if (!inputEaildata.includes('@')) {
      setisFormatWrong(true);
      return;
    }


    try {
      const response = await fetch('http://localhost:5000/api/Login_Data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ inputEmailData: inputEaildata , inputPWDData: inputPWDdata }),
      });
      const data = await response.json();
      console.log("Response data:", data);
      // 遍歷數據並輸出某些字段
      // 確保 data 是數組並且有內容
      if (Array.isArray(data) && data.length > 0) {
        if(inputEaildata != '' && inputPWDdata =='' && showInputPwd == false){
          setshowInputPwd(true)
          // data.forEach(item => {
          //   console.log("Username:", item.Username);
          //   setshowInputPwd(true)
          // });
        }else if(inputEaildata != '' && showInputPwd == true){
          if (inputPWDdata === '') {
            setisPWDEmpty(true);
            return;
          }
          data.forEach(item => {
            if(item.Password == ''){
              setisPWDWorng(true)
            }else{
              setisLoginSuccess(true)
            }
          });
          
        }

      } else {
        console.log("No data found");
        setisNoEmail(true)
        return;
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };



  return (
    <div className="login-container">
      <h1>登入</h1>
      <form>
        <div className="input-group">
          <div className="label-icon-wrapper">
            <label htmlFor="email">電子郵件</label>
            {/*----------------------------提示圖示(滑鼠移過去出現提示文字)--------------------------------- */}
            <FaExclamationCircle 
              className="icon-exclamation" 
              data-tooltip-id="email-tooltip"
              data-tooltip-content="請輸入有效的電子郵件地址"
            />
            <Tooltip
              id="email-tooltip"
              place="bottom" // 将 Tooltip 显示在图标下方
              effect="solid"
              className="custom-tooltip"
            />
            {/*----------------------------提示圖示(滑鼠移過去出現提示文字)--------------------------------- */}
          </div>
          <input type="email" id="email" name="email"  value={inputEaildata} onChange={inputEmaildataChange}/>
          {isEmpty && <label className="error-label">請輸入電子郵件</label>} {/* 條件渲染，當輸入框為空時顯示 */}
          {isFormatWrong && <label className="error-label">輸入的格式錯誤</label>} {/* 條件渲染，當輸入的值不包含'@'顯示 */}
          {isNoEmail && <label className="error-label">查無信箱</label>} {/* 條件渲染，當輸入的值不包含'@'顯示 */}
          {showInputPwd && 
          <>
          <label htmlFor="email">密碼</label>
          <input type="email" id="email1" name="email1"  value={inputPWDdata} onChange={inputPWDdataChange}/>
          {isPWDEmpty && <label className="error-label">請輸入密碼</label>} {/* 條件渲染，當輸入框為空時顯示 */}
          {isPWDWorng && <label className="error-label">密碼輸入錯誤！</label>} {/* 條件渲染，當輸入框為空時顯示 */}
          {isLoginSuccess && <label className="error-label">登入成功!</label>} {/* 條件渲染，當輸入框為空時顯示 */}
          </>
          } {/* 條件渲染，當輸入的值不包含'@'顯示 */}
        </div>
        <button type="submit" className="submit-btn" onClick={inputEailData}>下一步</button>
        <a href="#" className="help-link">登入時出現問題?</a>
        <div className="divider">或者</div>
        <button type="button" className="create-account-btn">聯絡官方建立帳戶</button>
      </form>
    </div>
  );
}

export default Index;
