import React, { useState, useEffect } from 'react';
import './InputImg.css';

function InputImg() {
    const [image, setImage] = useState("/images/market.jpg"); // 預設圖片
    const [fileName, setFileName] = useState(""); // 儲存檔案名稱
    const [showModal, setShowModal] = useState(false); // 控制模態視窗

    // 處理圖片選擇事件
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const imageUrl = URL.createObjectURL(file);
            setImage(imageUrl);
            setFileName(file.name);
        }
    };

    // 開啟模態視窗
    const openModal = () => {
        setShowModal(true);
    };

    // 關閉模態視窗
    const closeModal = () => {
        setShowModal(false);
    };

    return (
        <div className="input-img-container">
            <div className="image-container">
                <img src={image} alt="Title Image" className="title-image" />
                <div className="homeoverlay">
                    <p className="center-text">TitleImg</p>
                    <div className="upload-overlay">
                        <input
                            type="file"
                            accept="image/*"
                            onChange={handleImageChange}
                            id="image-input"
                            className="file-input"
                        />
                        <label htmlFor="image-input" className="upload-label">
                            <i className="fa fa-upload"></i> 更換圖片
                        </label>
                    </div>
                </div>
            </div>

            {/* 模態視窗 */}
            {showModal && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close-btn" onClick={closeModal}>
                            &times;
                        </span>
                        <img src={image} alt="預覽" className="modal-image" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default InputImg;
