import React, { useState } from 'react';
import './InputImg.css'; // 引入 CSS 樣式檔案

function InputImg() {
    const [image, setImage] = useState(null); // 用來儲存圖片的 URL
    const [fileName, setFileName] = useState(""); // 用來顯示選擇的檔案名稱
    const [showModal, setShowModal] = useState(false); // 控制模態視窗顯示與否

    // 處理圖片選擇事件
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const imageUrl = URL.createObjectURL(file);
            setImage(imageUrl);
            setFileName(file.name);
        }
    };

    // 開啟模態視窗
    const openModal = () => {
        setShowModal(true);
    };

    // 關閉模態視窗
    const closeModal = () => {
        setShowModal(false);
    };

    return (
        <div className="input-img-container">
            <div className="input-box">
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    id="image-input"
                    className="file-input"
                />
                <label htmlFor="image-input" className="file-label">
                    上傳圖片
                </label>
                {/* 眼睛圖示按鈕 */}
                {image && (
                    <button className="preview-btn" onClick={openModal}>
                        <i className="fa fa-eye"></i> 預覽
                    </button>
                )}
            </div>

            {/* 模態視窗 */}
            {showModal && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close-btn" onClick={closeModal}>
                            &times;
                        </span>
                        <img src={image} alt="預覽" className="modal-image" />
                    </div>
                </div>
            )}
        </div>
    );
}

export default InputImg;
